mapper.select:
select rno, bno, writer, content, regDate
from boardreplydto
where bno = #{bno}
order by rno asc;

mapper.update:
update boardreplydto 
	set
	content = #{content}
where rno = #{rno}
and bno = #{bno}
and replyPwd = #{replyPwd}

mapper.delete:
delete from boardreplydto
where rno = #{rno}
and bno = #{bno}
and replyPwd = #{replyPwd}

mapper.select:
select replyPwd
from boardreplydto
where rno = #{rno}
and bno = #{bno}
